package com.example.reakageappimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.currentBackStackEntryAsStateimport com.example.reakageapp.components.BottomNavigationBarimport com.example.reakageapp.components.TopBarimport com.example.reakageapp.screens.HomeScreenimport com.example.reakageapp.screens.LoginScreenimport com.example.reakageapp.screens.ProfileScreenimport com.example.reakageapp.screens.ReportListScreenimport com.example.reakageapp.screens.ReportScreenimport com.example.reakageapp.screens.SignUpScreenimport com.example.reakageapp.screens.SplashScreenimport com.example.reakageapp.theme.ReakageAppTheme@Composablefun NavigationGraph(navController: NavHostController) {    val navBackStackEntry = navController.currentBackStackEntryAsState().value    val currentRoute = navBackStackEntry?.destination?.route    // Hide TopBar and BottomNavigationBar for splash and login/signup screens    val showBottomBar = currentRoute in listOf("home", "report", "reports", "profile")    val showTopBar = showBottomBar    ReakageAppTheme {        Scaffold(            bottomBar = {                if (showBottomBar) BottomNavigationBar(navController = navController)            }        ) { padding ->            NavHost(                navController = navController,                startDestination = "splash",                modifier = Modifier.padding(padding)            ) {                composable("splash") { SplashScreen(navController) }                composable("login") { LoginScreen(navController) }                composable("signup") { SignUpScreen(navController) }                composable("home") { HomeScreen(navController) }                composable("report") { ReportScreen(navController) }                composable("reports") { ReportListScreen(navController) }                composable("profile") { ProfileScreen(navController) }            }        }    }}