// NavigationGraph.ktpackage com.example.reakageappimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.currentBackStackEntryAsStateimport com.example.reakageapp.components.BottomNavigationBarimport com.example.reakageapp.components.TopBarimport com.example.reakageapp.screens.*@Composablefun NavigationGraph(navController: NavHostController) {    val navBackStackEntry = navController.currentBackStackEntryAsState().value    val currentRoute = navBackStackEntry?.destination?.route    Scaffold(        topBar = { TopBar(title = currentRoute ?: "Water Reporter") },        bottomBar = { BottomNavigationBar(navController = navController) }    ) { padding ->        NavHost(navController, startDestination = "home", Modifier.padding(padding)) {            composable("home") { HomeScreen(navController) }            composable("report") { ReportScreen(navController) }            composable("reports") { ReportListScreen(navController) }            composable("profile") { ProfileScreen(navController) }        }    }}