package com.example.reakageapp.componentsimport androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.*import androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.ui.graphics.vector.ImageVectorimport androidx.navigation.NavControllersealed class NavItem(val route: String, val icon: ImageVector, val label: String) {    object Home : NavItem("home", Icons.Filled.Home, "Home")    object Report : NavItem("report", Icons.Filled.Notifications, "Report")    object Reports : NavItem("reports", Icons.Filled.List, "Reports")    object Profile : NavItem("profile", Icons.Filled.Person, "Profile")}@Composablefun BottomNavigationBar(navController: NavController) {    NavigationBar {        listOf(            NavItem.Home,            NavItem.Report,            NavItem.Reports,            NavItem.Profile        ).forEach { item ->            NavigationBarItem(                icon = { Icon(item.icon, contentDescription = item.label) },                label = { Text(item.label) },                selected = false,                onClick = {                    navController.navigate(item.route) {                        popUpTo(navController.graph.startDestinationId) { saveState = true }                        launchSingleTop = true                        restoreState = true                    }                }            )        }    }}