package com.example.reakageapp.componentsimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.*import androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Surfaceimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.blurimport androidx.compose.ui.draw.clipimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dp@Composablefun GlassCard(    modifier: Modifier = Modifier,    content: @Composable () -> Unit) {    Surface(        modifier = modifier            .clip(RoundedCornerShape(20.dp))            .blur(8.dp)            .background(                brush = Brush.verticalGradient(                    colors = listOf(Color(0x40FFFFFF), Color(0x20FFFFFF))                ),                shape = RoundedCornerShape(20.dp)            ),        color = Color.Transparent,        contentColor = MaterialTheme.colorScheme.onSurface    ) {        Box(modifier = Modifier.padding(16.dp)) {            content()        }    }}