package com.example.reakageapp.componentsimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Surfaceimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.blurimport androidx.compose.ui.draw.clipimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.RectangleShapeimport androidx.compose.ui.unit.dpimport com.example.reakageapp.theme.AquaLightimport com.example.reakageapp.theme.GlassTranslucentimport com.example.reakageapp.theme.RippleAccent@Composablefun GlassCard(    modifier: Modifier = Modifier,    content: @Composable () -> Unit) {    Surface(        modifier = modifier            .fillMaxSize() // Fills the entire available screen            .clip(RectangleShape) // No rounded corners            .background(                brush = Brush.verticalGradient(                    colors = listOf(                        GlassTranslucent.copy(alpha = 0.5f), // Subtle translucent start                        AquaLight.copy(alpha = 0.3f), // Soft aqua for water-like effect                        RippleAccent.copy(alpha = 0.2f) // Vibrant accent for depth                    )                ),                shape = RectangleShape            )            .blur(10.dp), // Retain blur for glassmorphism        color = Color.Transparent,        contentColor = MaterialTheme.colorScheme.onSurface    ) {        Box(modifier = Modifier.padding(24.dp)) { // Slightly increased padding for better content spacing            content()        }    }}