package com.example.reakageapp.screensimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.*import androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport com.example.reakageapp.components.GlassCard@Composablefun HomeScreen(navController: NavController) {    Box(        modifier = Modifier            .fillMaxSize()            .background(Brush.verticalGradient(listOf(Color(0xFF1565C0), Color(0xFF5E92F3))))            .padding(16.dp)    ) {        GlassCard {            Column(verticalArrangement = Arrangement.spacedBy(16.dp)) {                Button(onClick = { navController.navigate("report") }, modifier = Modifier.fillMaxWidth()) {                    Text("Report Water Issue")                }                Button(onClick = { navController.navigate("reports") }, modifier = Modifier.fillMaxWidth()) {                    Text("View Reports")                }                Button(onClick = { navController.navigate("profile") }, modifier = Modifier.fillMaxWidth()) {                    Text("My Profile")                }            }        }    }}