package com.example.reakageapp.screensimport androidx.compose.foundation.Imageimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.*import androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.layout.ContentScaleimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.navigation.NavControllerimport com.example.reakageapp.components.GlassCard@Composablefun HomeScreen(navController: NavController) {    Box(        modifier = Modifier            .fillMaxSize()            .background(                Brush.verticalGradient(                    colors = listOf(Color(0xFF0D47A1), Color(0xFF42A5F5))                )            )            .padding(24.dp)    ) {        GlassCard(            modifier = Modifier                .fillMaxWidth()                .align(Alignment.Center)                .padding(16.dp)        ) {            Column(                modifier = Modifier                    .padding(24.dp)                    .fillMaxWidth(),                horizontalAlignment = Alignment.CenterHorizontally,                verticalArrangement = Arrangement.spacedBy(24.dp),            ) {                Text(                    text = "Welcome to Reakage App",                    style = MaterialTheme.typography.headlineLarge.copy(                        color = Color.White,                        fontWeight = FontWeight.ExtraBold,                        fontSize = 32.sp                    )                )                Text(                    text = "Your trusted app for reporting water issues and tracking repairs efficiently.",                    style = MaterialTheme.typography.bodyLarge.copy(                        color = Color.White.copy(alpha = 0.8f),                        fontSize = 18.sp                    ),                    modifier = Modifier.padding(horizontal = 8.dp),                    textAlign = TextAlign.Center                )                // Android built-in drawable                Image(                    painter = painterResource(id = android.R.drawable.ic_menu_compass),                    contentDescription = "Icon representing navigation or location",                    contentScale = ContentScale.Fit,                    modifier = Modifier                        .height(180.dp)                        .fillMaxWidth()                )                Column(                    verticalArrangement = Arrangement.spacedBy(16.dp),                    modifier = Modifier.fillMaxWidth()                ) {                    Button(                        onClick = { navController.navigate("report") },                        modifier = Modifier.fillMaxWidth(),                        colors = ButtonDefaults.buttonColors(containerColor = Color(0xFF1565C0))                    ) {                        Text(text = "Report Water Issue")                    }                    Button(                        onClick = { navController.navigate("reports") },                        modifier = Modifier.fillMaxWidth(),                        colors = ButtonDefaults.buttonColors(containerColor = Color(0xFF1E88E5))                    ) {                        Text(text = "View Reports")                    }                    Button(                        onClick = { navController.navigate("profile") },                        modifier = Modifier.fillMaxWidth(),                        colors = ButtonDefaults.buttonColors(containerColor = Color(0xFF42A5F5))                    ) {                        Text(text = "My Profile")                    }                }            }        }    }}