package com.example.reakageapp.screensimport androidx.compose.animation.core.*import androidx.compose.foundation.Imageimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.*import androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.runtime.getValueimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.alphaimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.layout.ContentScaleimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.text.style.TextAlignimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport androidx.navigation.NavControllerimport com.example.reakageapp.components.GlassCardimport com.example.reakageapp.theme.AquaLightimport com.example.reakageapp.theme.DeepOceanimport com.example.reakageapp.theme.RippleAccentimport com.example.reakageapp.theme.glassmorphism@Composablefun HomeScreen(navController: NavController) {    // Fade-in animation for the GlassCard    val alpha by animateFloatAsState(        targetValue = 1f,        animationSpec = tween(durationMillis = 800, easing = LinearOutSlowInEasing),        label = "FadeInAnimation"    )    Box(        modifier = Modifier            .fillMaxSize()            .background(                Brush.linearGradient(                    colors = listOf(AquaLight, RippleAccent, DeepOcean),                    start = androidx.compose.ui.geometry.Offset(0f, 0f),                    end = androidx.compose.ui.geometry.Offset(0f, Float.POSITIVE_INFINITY)                )            )            .padding(16.dp)    ) {        GlassCard(            modifier = Modifier                .fillMaxSize()                .glassmorphism() // Apply glassmorphism modifier                .alpha(alpha) // Apply fade-in animation                .align(Alignment.Center)        ) {            Column(                modifier = Modifier                    .fillMaxSize()                    .padding(32.dp),                horizontalAlignment = Alignment.CenterHorizontally,                verticalArrangement = Arrangement.SpaceBetween            ) {                // Header Section                Column(                    horizontalAlignment = Alignment.CenterHorizontally,                    verticalArrangement = Arrangement.spacedBy(16.dp)                ) {                    Text(                        text = "Welcome to Reakage",                        style = MaterialTheme.typography.displayMedium.copy(                            color = DeepOcean,                            fontWeight = FontWeight.Bold,                            fontSize = 36.sp                        ),                        textAlign = TextAlign.Center                    )                    Text(                        text = "Report water leaks and track repairs with ease.",                        style = MaterialTheme.typography.bodyLarge.copy(                            color = DeepOcean.copy(alpha = 0.85f),                            fontSize = 20.sp,                            lineHeight = 28.sp                        ),                        modifier = Modifier.padding(horizontal = 16.dp),                        textAlign = TextAlign.Center                    )                }                // Water-themed image (placeholder for actual resource)                Image(                    painter = painterResource(id = android.R.drawable.ic_menu_info_details), // Placeholder; replace with water-themed vector                    contentDescription = "Water droplet icon",                    contentScale = ContentScale.Fit,                    modifier = Modifier                        .size(200.dp)                        .padding(16.dp)                )                // Button Section                Column(                    verticalArrangement = Arrangement.spacedBy(12.dp),                    modifier = Modifier.fillMaxWidth()                ) {                    Button(                        onClick = { navController.navigate("report") },                        modifier = Modifier                            .fillMaxWidth()                            .height(56.dp),                        colors = ButtonDefaults.buttonColors(                            containerColor = DeepOcean,                            contentColor = MaterialTheme.colorScheme.onPrimary                        ),                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)                    ) {                        Text(                            text = "Report Water Issue",                            fontSize = 16.sp,                            fontWeight = FontWeight.Medium                        )                    }                    Button(                        onClick = { navController.navigate("reports") },                        modifier = Modifier                            .fillMaxWidth()                            .height(56.dp),                        colors = ButtonDefaults.buttonColors(                            containerColor = RippleAccent,                            contentColor = MaterialTheme.colorScheme.onPrimary                        ),                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)                    ) {                        Text(                            text = "View Reports",                            fontSize = 16.sp,                            fontWeight = FontWeight.Medium                        )                    }                    Button(                        onClick = { navController.navigate("profile") },                        modifier = Modifier                            .fillMaxWidth()                            .height(56.dp),                        colors = ButtonDefaults.buttonColors(                            containerColor = AquaLight,                            contentColor = MaterialTheme.colorScheme.onPrimary                        ),                        elevation = ButtonDefaults.buttonElevation(defaultElevation = 4.dp)                    ) {                        Text(                            text = "My Profile",                            fontSize = 16.sp,                            fontWeight = FontWeight.Medium                        )                    }                }            }        }    }}