package com.example.reakageapp.screensimport androidx.compose.foundation.Imageimport androidx.compose.foundation.layout.*import androidx.compose.foundation.text.KeyboardOptionsimport androidx.compose.material3.*import androidx.compose.material3.Textimport androidx.compose.runtime.*import androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.text.input.ImeActionimport androidx.compose.ui.unit.dpimport androidx.navigation.NavControllerimport androidx.compose.ui.text.input.PasswordVisualTransformationimport com.example.reakageapp.theme.glassmorphismimport com.example.reakageapp.R@Composablefun LoginScreen(navController: NavController) {    var email by remember { mutableStateOf("") }    var password by remember { mutableStateOf("") }    Column(        modifier = Modifier            .fillMaxSize()            .padding(16.dp),        horizontalAlignment = Alignment.CenterHorizontally,        verticalArrangement = Arrangement.Center    ) {        Text(            text = "Login",            style = MaterialTheme.typography.headlineLarge,            modifier = Modifier.padding(bottom = 32.dp)        )        Box(            modifier = Modifier                .glassmorphism()                .padding(16.dp)        ) {            Column(                horizontalAlignment = Alignment.CenterHorizontally,                verticalArrangement = Arrangement.Center,                modifier = Modifier.fillMaxWidth()            ) {                OutlinedTextField(                    value = email,                    onValueChange = { email = it },                    label = { Text("Email") },                    keyboardOptions = KeyboardOptions.Default.copy(                        imeAction = ImeAction.Next                    ),                    modifier = Modifier.fillMaxWidth(),                    singleLine = true                )                Spacer(modifier = Modifier.height(16.dp))                OutlinedTextField(                    value = password,                    onValueChange = { password = it },                    label = { Text("Password") },                    visualTransformation = PasswordVisualTransformation(),                    keyboardOptions = KeyboardOptions.Default.copy(                        imeAction = ImeAction.Done                    ),                    modifier = Modifier.fillMaxWidth(),                    singleLine = true                )                Spacer(modifier = Modifier.height(24.dp))                Button(                    onClick = { navController.navigate("home") },                    modifier = Modifier.fillMaxWidth()                ) {                    Text("Login")                }                Spacer(modifier = Modifier.height(16.dp))                SocialSignInButtons()            }        }    }}@Composablefun SocialSignInButtons() {    Row(        modifier = Modifier.fillMaxWidth(),        horizontalArrangement = Arrangement.Center,        verticalAlignment = Alignment.CenterVertically    ) {        IconButton(onClick = { /* Handle Google sign-in */ }) {            Image(painter = painterResource(id = R.drawable.google_logo), contentDescription = "Google SignIn")        }        Spacer(modifier = Modifier.width(16.dp))        IconButton(onClick = { /* Handle Facebook sign-in */ }) {            Image(painter = painterResource(id = R.drawable.facebook_logo), contentDescription = "Facebook SignIn")        }        Spacer(modifier = Modifier.width(16.dp))        IconButton(onClick = { /* Handle Instagram sign-in */ }) {            Image(painter = painterResource(id = R.drawable.instagram_logo), contentDescription = "Instagram SignIn")        }    }}