package com.example.reakageapp.themeimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.*import androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material3.MaterialThemeimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.unit.dpimport com.example.reakageapp.theme.TranslucentWhite// Glassmorphism Modifierfun Modifier.glassmorphism() = this    .background(        brush = Brush.verticalGradient(            colors = listOf(                TranslucentWhite.copy(alpha = 0.2f),                TranslucentWhite.copy(alpha = 0.4f)            ),            startY = 0.0f,            endY = 1000f        ),        shape = RoundedCornerShape(20.dp) // Rounded corners    )    .padding(16.dp)  // Add padding to give space around the content    .fillMaxWidth()   // Make sure it takes the full width