package com.example.reakageapp.themeimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.*import androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.graphics.RectangleShapeimport androidx.compose.ui.unit.dp// Glassmorphism Modifierfun Modifier.glassmorphism() = this    .background(        brush = Brush.verticalGradient(            colors = listOf(                GlassTranslucent.copy(alpha = 0.3f), // Subtle translucent start                AquaLight.copy(alpha = 0.2f), // Soft aqua for water-like effect                RippleAccent.copy(alpha = 0.15f) // Vibrant accent for depth            ),            startY = 0.0f,            endY = 800f // Reduced range for smoother gradient        ),        shape = RectangleShape // No rounded corners to match GlassCard    )    .padding(20.dp) // Slightly increased padding for better content spacing    .fillMaxWidth() // Maintain full width