package com.example.reakageapp.themeimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.sp// Custom Colors for Water-Themed Glassmorphismval AquaLight = Color(0xFF70C1FF) // Soft aqua for backgrounds, evoking clear waterval DeepOcean = Color(0xFF004D99) // Deep blue for text and primary accentsval WaveWhite = Color(0xFFF5FAFF) // Off-white for backgrounds, resembling sea foamval GlassTranslucent = Color(0xB3E6F0FF) // Semi-transparent blue-white for glassmorphismval RippleAccent = Color(0xFF00A3CC) // Vibrant aqua for secondary accentsprivate val WaterColorScheme = lightColorScheme(    primary = DeepOcean,    secondary = RippleAccent,    background = WaveWhite,    surface = GlassTranslucent.copy(alpha = 0.35f), // Subtle translucency for glassmorphism    onPrimary = WaveWhite,    onSecondary = WaveWhite,    onBackground = DeepOcean,    onSurface = DeepOcean,    tertiary = AquaLight // Additional color for highlights or buttons)@Composablefun ReakageAppTheme(content: @Composable () -> Unit) {    MaterialTheme(        colorScheme = WaterColorScheme,        typography = Typography(            // Customize typography for a fluid, modern look            displayLarge = Typography().displayLarge.copy(fontFamily = androidx.compose.ui.text.font.FontFamily.SansSerif),            bodyLarge = Typography().bodyLarge.copy(lineHeight = 28.sp), // Increased line height for readability            labelSmall = Typography().labelSmall.copy(color = DeepOcean) // Consistent text color        ),        shapes = Shapes(            // Rounded shapes to mimic water droplets            small = RoundedCornerShape(12.dp),            medium = RoundedCornerShape(16.dp),            large = RoundedCornerShape(20.dp)        ),        content = content    )}